const slides=document.querySelectorAll(".headline-slider-item"),pagination=document.querySelectorAll(".slider-button"),removeCurrent=()=>{pagination.forEach(a=>{a.classList.remove("slider-button-current")}),slides.forEach(a=>{a.classList.add("visually-hidden")})};pagination.forEach((a,b)=>{a.addEventListener("click",a=>{removeCurrent(),a.target.classList.add("slider-button-current"),slides[b].classList.remove("visually-hidden")})})
